const PostList = ({posts}) => {
    return ( 
     <div>

       {posts.map(post => (
         <div className='container row border border-warning mx-auto text-center m-5 w-25'  key={post.id}>
           <div className="col"> 
            <h2>{(post.title).limit(2)}</h2>
            <p>{post.description}</p>
           </div>
         </div> 
       ))}
      </div>
     );
}
 
export default PostList;
